INSERT INTO PUBLIC.ARTICLE(ARTICLE_ID, TITLE, INGRESS, TEXT, AUTHOR, IMAGE_PATH, DATE_MADE) VALUES(1, 'Dette er et eksempel innlegg', STRINGDECODE('Her st\u00e5r det forskjellige ting som virker interessant og f\u00e5r folk til \u00e5 trykke p\u00e5 denne artikkelen. Her kan det st\u00e5 en del. Ofte er det en introduksjon til artikkelen.'), STRINGDECODE('<p>Dette er kroppen til artikkelen. Den skal v&aelig;;re bulken av artikkelen og inneholde mest av informasjonen. Det er veldig merkelig at den vokser mens jeg skriver, og litt morsom og. Vi kan etter behov legge till ett bilde ogs&aring;;. Dette bilde blir lastet opp til brukerens egen mappe. Alle har hver sine mappe under public/upload/[brukernavn]. I denne mappen er det lagringsplass for alle dine bilder. Bildene dine kommer ikke til &aring;; endre p&aring;; navnet sitt hvis ikke det er en kollisjon med et annet navn. Dersom det er kollisjon prefixer vi den fila med et tall og en underscore. Dersom dette tallet er tatt g&aring;;r tallet opp og opp inntil tallet er stort nok til &aring;; kunne f&aring;; lagre fila uten problem. Brukeren kan ogs&aring;; skape arransjementer. Dette gj&oslash;;res via en ekstra knapp. Brukeren kan deretter stille inn flere parametre.</p>\r\n'), 1, '/assets/upload/hybrid/happy cat.jpg', TIMESTAMP '2015-02-12 00:05:08.963');
